import React, { useState, useEffect, useCallback, useMemo } from "react";
import { motion } from "framer-motion";
import { FiX, FiChevronLeft, FiChevronRight } from "react-icons/fi";
import { useInView } from "react-intersection-observer";
import "../styles/main.css";
import "../styles/responsive.css";
import "../styles/gallery.css";
import { photos, photoCategories } from "../data/galleryData";

const Gallery = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [activeCategory, setActiveCategory] = useState("all");
  const [loadedImages, setLoadedImages] = useState(new Set());

  const filteredPhotos = useMemo(
    () =>
      activeCategory === "all"
        ? photos
        : photos.filter((photo) => photo.category === activeCategory),
    [activeCategory]
  );

  // Lazy Image Component
  const LazyImage = ({ photo, onClick }) => {
    const { ref, inView } = useInView({
      threshold: 0.1,
      triggerOnce: true,
    });

    const [imageLoaded, setImageLoaded] = useState(false);

    const handleImageLoad = useCallback(() => {
      setImageLoaded(true);
      setLoadedImages((prev) => new Set([...prev, photo.src]));
    }, [photo.src]);

    return (
      <motion.div
        ref={ref}
        className="gallery-item"
        whileHover={{ scale: 1.02 }}
        onClick={onClick}
        layoutId={`photo-${photo.id}`}
      >
        {inView && (
          <>
            <img
              src={photo.src}
              alt={photo.title}
              loading="lazy"
              onLoad={handleImageLoad}
              style={{
                opacity: imageLoaded ? 1 : 0,
                transition: "opacity 0.3s ease",
              }}
            />
            {!imageLoaded && (
              <div className="image-placeholder">
                <div className="loading-spinner"></div>
              </div>
            )}
            <div className="photo-overlay">
              <h3>{photo.title}</h3>
            </div>
          </>
        )}
      </motion.div>
    );
  };

  // Navigation functions
  const goToPrevious = useCallback(() => {
    if (!selectedImage) return;

    const currentIndex = filteredPhotos.findIndex(
      (photo) => photo.id === selectedImage.id
    );
    const prevIndex = currentIndex > 0 ? currentIndex - 1 : filteredPhotos.length - 1;
    setSelectedImage(filteredPhotos[prevIndex]);
  }, [selectedImage, filteredPhotos]);

  const goToNext = useCallback(() => {
    if (!selectedImage) return;

    const currentIndex = filteredPhotos.findIndex(
      (photo) => photo.id === selectedImage.id
    );
    const nextIndex = currentIndex < filteredPhotos.length - 1 ? currentIndex + 1 : 0;
    setSelectedImage(filteredPhotos[nextIndex]);
  }, [selectedImage, filteredPhotos]);

  // Keyboard navigation
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (!selectedImage) return;

      if (e.key === "ArrowLeft") {
        goToPrevious();
      } else if (e.key === "ArrowRight") {
        goToNext();
      } else if (e.key === "Escape") {
        setSelectedImage(null);
      }
    };

    window.addEventListener("keydown", handleKeyPress);
    return () => window.removeEventListener("keydown", handleKeyPress);
  }, [selectedImage, goToPrevious, goToNext]);

  const handleCategoryChange = useCallback((category) => {
    setActiveCategory(category);
  }, []);

  const openModal = useCallback((photo) => {
    setSelectedImage(photo);
  }, []);

  const closeModal = useCallback(() => {
    setSelectedImage(null);
  }, []);

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="page-container gallery-page"
    >
      <h1>Photography Gallery</h1>

      <div className="category-filter">
        <button
          onClick={() => handleCategoryChange("all")}
          className={activeCategory === "all" ? "active" : ""}
        >
          All
        </button>
        {photoCategories.map((category) => (
          <button
            key={category.id}
            onClick={() => handleCategoryChange(category.id)}
            className={activeCategory === category.id ? "active" : ""}
          >
            {category.name}
          </button>
        ))}
      </div>

      <div className="gallery-grid">
        {filteredPhotos.map((photo) => (
          <LazyImage
            key={photo.id}
            photo={photo}
            onClick={() => openModal(photo)}
          />
        ))}
      </div>

      {selectedImage && (
        <motion.div
          className="image-modal"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={closeModal}
        >
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>{selectedImage.title}</h2>
              <div className="image-counter">
                {
                  filteredPhotos.findIndex(photo => photo.id === selectedImage.id) + 1
                }{" "}
                of {filteredPhotos.length}
              </div>
            </div>

            <div className="modal-image-container">
              <motion.img
                key={selectedImage.id}
                src={selectedImage.src}
                alt={selectedImage.title}
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                transition={{ duration: 0.3 }}
                className="modal-image"
              />
            </div>

            <button
              className="nav-button nav-button-left"
              onClick={(e) => {
                e.stopPropagation();
                goToPrevious();
              }}
              aria-label="Previous image"
            >
              <FiChevronLeft />
            </button>

            <button
              className="nav-button nav-button-right"
              onClick={(e) => {
                e.stopPropagation();
                goToNext();
              }}
              aria-label="Next image"
            >
              <FiChevronRight />
            </button>

            <div className="modal-footer">
              <button onClick={closeModal} className="btn close-btn">
                <FiX /> Close
              </button>
            </div>
          </div>
        </motion.div>
      )}
    </motion.div>
  );
};

export default Gallery;
