import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import FeaturedWork from '../components/FeaturedWork';
import Skills from '../components/Skills';
import '../styles/main.css'

const Home = () => {
  // Updated useInView hooks for section animations
  const sectionViewOptions = {
    threshold: 0.1,
    triggerOnce: true
  };

  const [aboutRef, aboutInView] = useInView(sectionViewOptions);
  const [featuredWorksRef, featuredWorksInView] = useInView(sectionViewOptions);
  const [skillsRef, skillsInView] = useInView(sectionViewOptions);
  // const [contactRef, contactInView] = useInView(sectionViewOptions); // If you add a Contact section here


  // Expanded featuredWorks with placeholders
  const featuredWorks = [
    {
      id: 1,
      title: "Music Video - 'Ek Kamra'",
      role: "Director",
      year: "2025",
      description: "Directed and shot the video with a minimalist aesthetic and a strong emotional narrative",
      image: "/images/ek-kamra.jpg"
    },
    {
      id: 2,
      title: "BoxoutFM & Premise",
      role: "Videographer",
      year: "2024-2025",
      description: "Covered live DJ sets and captured underground electronic music culture",
      image: "/images/boxout.jpg"
    },
    {
      id: 3,
      title: "Your Project Title 3",
      role: "Your Role (e.g., Photographer, Editor)",
      year: "Year",
      description: "Briefly describe this project, highlighting your skills and achievements.",
      image: "/images/placeholder-project3.jpg" // Replace with actual image path
    },
    {
      id: 4,
      title: "Your Project Title 4",
      role: "Your Role",
      year: "Year",
      description: "Another project description focusing on your contributions and expertise.",
      image: "/images/placeholder-project4.jpg" // Replace with actual image path
    }
    // Add more project objects as needed
  ];

  return (
    <motion.div 
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="home"
    >
      <section className="hero">
        <motion.h1
          initial={{ y: -50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.8 }}
        >
          bluepolaroid
        </motion.h1>
        <motion.h3
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.4 }}
        >
          I'm a cinematographer and visual storyteller with hands-on experience in directing, shooting, and editing across diverse music, culture, and narrative-driven projects. From high-energy underground DJ sets to emotionally charged music videos, I bring stories to life with precision, creativity, and heart.
        </motion.h3>
      </section>

      {/* About Me Section */}
      <section ref={aboutRef} className="about-me content-section container">
        <motion.h2 /* Changed to h2 for semantic structure */
          initial={{ opacity: 0, y: 20 }}
          animate={aboutInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6 }}
        >
          About Me
        </motion.h2>
        <motion.p
          initial={{ opacity: 0, y: 20 }}
          animate={aboutInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          {/* Replace this with your 2-line description from your resume */}
          A dynamic Photographer & Videographer, I specialize in crafting compelling visual stories and bringing creative concepts to vibrant life. My expertise spans from documentary filmmaking to dynamic music video production, always aiming for impactful and memorable results.
        </motion.p>
      </section>

      <section ref={featuredWorksRef} className="featured-works content-section container">
        <motion.h3
          initial={{ opacity: 0, y: 20 }}
          animate={featuredWorksInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6 }}
        >
          Featured Works
        </motion.h3>
        <div className="works-grid">
          {featuredWorks.map((work, index) => (
            <FeaturedWork 
              key={work.id}
              work={work}
              index={index}
              inView={featuredWorksInView} // Use the specific inView for this section
            />
          ))}
        </div>
      </section>

      {/* Skills Section */}
      <section ref={skillsRef} className="skills-display-section content-section container">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={skillsInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          <Skills />
        </motion.div>
      </section>
    </motion.div>
  );
};

export default Home;
