import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const Footer = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const mailtoLink = `mailto:raghavsobti37@gmail.com?subject=Message from ${formData.name}&body=${formData.message}%0A%0AFrom: ${formData.email}`;
    window.location.href = mailtoLink;
  };

  return (
    <motion.footer
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ delay: 0.5 }}
      className="footer"
    >
      <div className="footer-content">
        <div className="contact-info">
          <h3>Let's Connect</h3>
          <p>Phone: +91 85914 99393</p>
          <p>Email: raghavsobti37@gmail.com</p>
        </div>

        <div className="footer-nav">
          <h3>Navigate</h3>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/gallery">Gallery</Link></li>
            <li><a href="#contact">Contact</a></li>
          </ul>
        </div>

        <div className="footer-form">
          <h3>Contact Form</h3>
          <form onSubmit={handleSubmit}>
            <input 
              type="text" 
              name="name" 
              placeholder="Your Name" 
              required 
              onChange={handleChange} 
              value={formData.name} 
            />
            <input 
              type="email" 
              name="email" 
              placeholder="Your Email" 
              required 
              onChange={handleChange} 
              value={formData.email} 
            />
            <textarea 
              name="message" 
              placeholder="Your Message" 
              required 
              onChange={handleChange} 
              value={formData.message} 
            ></textarea>
            <button type="submit">Send</button>
          </form>
        </div>

        <div className="copyright">
          <p>&copy; {new Date().getFullYear()} Raghav Raj Sobti. All rights reserved.</p>
          <p>Designed with passion for visual storytelling.</p>
        </div>
      </div>
    </motion.footer>
  );
};

export default Footer;
