import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { FaInstagram, FaYoutube, FaEnvelope, FaWhatsapp, FaHome, FaImages } from 'react-icons/fa'; // Added FaHome and FaImages
import '../styles/main.css'

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const socialLinks = [
    { icon: <FaInstagram />, url: 'https://instagram.com/bluepolaroid05' },
    { icon: <FaYoutube />, url: 'https://youtube.com/@bluepolaroid05' },
    { icon: <FaWhatsapp />, url: 'https://wa.me/918591499393' },
    { icon: <FaEnvelope />, url: 'mailto:raghavsobti37@gmail.com' }
  ];

  return (
    <>
      <motion.nav
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ duration: 0.5 }}
        className="navbar"
      >
        <div className="navbar-container">
          <Link to="/" className="logo-icon" onClick={() => setIsOpen(false)} aria-label="Home">
            <FaHome size={28} />
          </Link>

          {/* Projects Link for Desktop */}
          <Link to="/projects" className="nav-link projects-link-desktop" onClick={() => setIsOpen(false)}>
            <FaImages style={{ marginRight: '8px' }} /> Projects
          </Link>

          {/* Social icons for desktop view */}
          {/* These might be hidden on mobile via CSS (e.g., .nav-social-icons-desktop { display: none; } @media mobile) */}
          <div className="nav-social-icons nav-social-icons-desktop">
            {socialLinks.map((link, index) => (
              <a
                key={index}
                href={link.url}
                target="_blank"
                rel="noopener noreferrer"
                className="social-icon"
              >
                {link.icon}
              </a>
            ))}
          </div>

          <button
            className={`hamburger ${isOpen ? 'active' : ''}`}
            onClick={() => setIsOpen(!isOpen)}
            aria-label="Toggle menu"
            aria-expanded={isOpen}
          >
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
      </motion.nav>

      {/* Mobile Menu */}
      {isOpen && (
        <motion.div
          className="mobile-menu" // This class will need styling in main.css
          initial={{ opacity: 0, y: -20 }} // Or height: 0
          animate={{ opacity: 1, y: 0 }}   // Or height: 'auto'
          exit={{ opacity: 0, y: -20 }}     // Or height: 0
          transition={{ duration: 0.3 }}
        >
          {/* Projects Link for Mobile */}
          <Link to="/projects" className="nav-link mobile-nav-link" onClick={() => setIsOpen(false)}>
            <FaImages style={{ marginRight: '8px' }} /> Projects
          </Link>

          {/* Social Icons for Mobile */}
          <div className="nav-social-icons nav-social-icons-mobile">
            {socialLinks.map((link, index) => (
              <a
                key={index}
                href={link.url}
                target="_blank"
                rel="noopener noreferrer"
                className="social-icon"
                onClick={() => setIsOpen(false)} // Close menu on link click
              >
                {link.icon}
              </a>
            ))}
          </div>
        </motion.div>
      )}
    </>
  );
};

export default Navbar;
