import React from 'react';
import { motion } from 'framer-motion';
import './styles/home.css';

const Home = () => {
  return (
    <div className="home-page">
      <div className="hero-container">
        {/* Background image with low opacity */}
        <div className="hero-background">
          <img 
            src="/images/hero-background.jpg" 
            alt="Cinematic Background" 
            className="desaturated-image"
          />
        </div>
        
        {/* Profile image with decorative elements */}
        <div className="profile-container">
          <div className="profile-image-wrapper">
            <img 
              src="/images/profile-photo.jpg" 
              alt="Raghav Raj Sobti" 
              className="profile-image"
            />
            
            {/* Decorative elements */}
            <div className="decorative-number">37</div>
            <div className="decorative-text passionate">PASSIONATE</div>
            <div className="decorative-text creative">CREATIVE</div>
            <div className="decorative-text visual">VISUAL STORYTELLER</div>
            <div className="decorative-text curious">CURIOUS</div>
          </div>
          
          <div className="hero-text">
            <motion.h1 
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              RAGHAV RAJ SOBTI
            </motion.h1>
            <motion.h2
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              PHOTOGRAPHER & VIDEOGRAPHER
            </motion.h2>
            <motion.p
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.4 }}
            >
              Bringing stories to life through compelling visuals and cinematic storytelling
            </motion.p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
