import React from "react";
import { FiCamera, FiCalendar } from "react-icons/fi";

const CameraMetadata = ({ photo }) => {
  // Don't render anything if there's no metadata
  if (!photo.hasMetadata) {
    return null;
  }

  return (
    <div className="camera-metadata">
      {/* Camera and Lens info */}
      {(photo.camera || photo.lens) && (
        <div className="metadata-section">
          <FiCamera className="metadata-icon" />
          <div className="metadata-content">
            {photo.camera && <p><strong>Camera:</strong> {photo.camera}</p>}
            {photo.lens && <p><strong>Lens:</strong> {photo.lens}</p>}
          </div>
        </div>
      )}
      
      {/* Camera Settings */}
      {photo.settings && (
        <div className="metadata-section">
          <div className="camera-settings">
            {photo.settings.aperture && (
              <div className="setting-item">
                <span className="setting-label">Aperture:</span>
                <span className="setting-value">{photo.settings.aperture}</span>
              </div>
            )}
            {photo.settings.shutter && (
              <div className="setting-item">
                <span className="setting-label">Shutter:</span>
                <span className="setting-value">{photo.settings.shutter}</span>
              </div>
            )}
            {photo.settings.iso && (
              <div className="setting-item">
                <span className="setting-label">ISO:</span>
                <span className="setting-value">{photo.settings.iso}</span>
              </div>
            )}
            {photo.settings.focalLength && (
              <div className="setting-item">
                <span className="setting-label">Focal Length:</span>
                <span className="setting-value">{photo.settings.focalLength}</span>
              </div>
            )}
          </div>
        </div>
      )}
      
      {/* Date */}
      {photo.date && (
        <div className="metadata-section">
          <FiCalendar className="metadata-icon" />
          <div className="metadata-content">
            <p><strong>Date:</strong> {new Date(photo.date).toLocaleDateString('en-US', { 
              year: 'numeric', 
              month: 'long', 
              day: 'numeric' 
            })}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default CameraMetadata;
