import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { motion } from 'framer-motion';
import { FaHome, FaUser, FaImages, FaEnvelope, FaClipboardList } from 'react-icons/fa';
import '../styles/main.css';

const Navbar = () => {
  const location = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const navItems = [
    { path: '/', label: 'HOME' },
    { path: '/about', label: 'ABOUT' },
    { path: '/projects', label: 'PROJECTS' },
    { path: '/services', icon: <FaClipboardList />, label: 'SERVICES' },
    { path: '/contact', icon: <FaEnvelope />, label: 'CONTACT' }
  ];

  return (
    <>
      {/* Desktop Navbar */}
      <motion.nav 
        className="vertical-navbar desktop-nav"
        initial={{ opacity: 0, x: -50 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="nav-logo">
          <span>BLUEPOLAROID</span>
        </div>
        
        <ul className="nav-links">
          {navItems.map((item) => (
            <li key={item.path} className={location.pathname === item.path ? 'active' : ''}>
              <Link to={item.path}>
                {item.icon}
                <span>{item.label}</span>
              </Link>
            </li>
          ))}
        </ul>
        
        <div className="nav-footer">
          <p>Â© 2025 BLUEPOLAROID</p>
        </div>
      </motion.nav>

      {/* Mobile Navbar */}
      <motion.nav 
        className="mobile-nav"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.3 }}
      >
        <div className="mobile-nav-header">
          <div className="mobile-logo">BLUEPOLAROID</div>
          <button 
            className={`hamburger ${mobileMenuOpen ? 'open' : ''}`}
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
          >
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>

        {mobileMenuOpen && (
          <div className="mobile-menu">
            <ul>
              {navItems.map((item) => (
                <li 
                  key={item.path} 
                  className={location.pathname === item.path ? 'active' : ''}
                  onClick={() => setMobileMenuOpen(false)}
                >
                  <Link to={item.path}>
                    {item.icon}
                    <span>{item.label}</span>
                  </Link>
                </li>
              ))}
            </ul>
          </div>
        )}
      </motion.nav>
    </>
  );
};

export default Navbar;
