import React, { useState, useEffect, useRef } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FiHome, FiUser, FiCamera, FiFilm, FiMail, FiMenu, FiX } from 'react-icons/fi';

const Navbar = () => {
  const [isDesktopCollapsed, setIsDesktopCollapsed] = useState(true);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const location = useLocation();
  const hoverTimeoutRef = useRef(null);

  const handleMouseEnterDesktop = () => {
    clearTimeout(hoverTimeoutRef.current);
    setIsDesktopCollapsed(false);
  };

  const handleMouseLeaveDesktop = () => {
    hoverTimeoutRef.current = setTimeout(() => {
      setIsDesktopCollapsed(true);
    }, 200); // Optional delay before collapsing again
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen((prev) => !prev);
  };

  const navLinks = [
    { path: '/', label: 'Home', icon: <FiHome /> },
    { path: '/about', label: 'About', icon: <FiUser /> },
    { path: '/photography', label: 'Photography', icon: <FiCamera /> },
    { path: '/films', label: 'Films', icon: <FiFilm /> },
    { path: '/contact', label: 'Contact', icon: <FiMail /> },
  ];

  return (
    <>
      {/* Desktop Navbar */}
      <nav
        className={`vertical-navbar ${isDesktopCollapsed ? 'collapsed' : ''}`}
        onMouseEnter={handleMouseEnterDesktop}
        onMouseLeave={handleMouseLeaveDesktop}
      >
        <div className="nav-logo">
          <span className="logo-full">RRS</span>
          <span className="logo-collapsed">R</span>
        </div>
        <ul className="nav-links">
          {navLinks.map((link) => (
            <li key={link.path} className={location.pathname === link.path ? 'active' : ''}>
              <Link to={link.path}>
                <span className="nav-icon">{link.icon}</span>
                <span className="nav-link-text">{link.label}</span>
              </Link>
            </li>
          ))}
        </ul>
        <div className="nav-footer">
          <span className="nav-link-text">© 2025 RRS</span>
        </div>
      </nav>

      {/* Mobile Navbar */}
      <div className="mobile-navbar">
        <Link to="/" className="mobile-nav-logo">RRS</Link>
        <button className="mobile-hamburger" onClick={toggleMobileMenu}>
          {isMobileMenuOpen ? <FiX /> : <FiMenu />}
        </button>
      </div>

      {isMobileMenuOpen && (
        <div className="mobile-dropdown-menu">
          <ul className="mobile-nav-links">
            {navLinks.map((link) => (
              <li key={link.path} className={location.pathname === link.path ? 'active' : ''}>
                <Link to={link.path} onClick={() => setIsMobileMenuOpen(false)}>
                  {link.label}
                </Link>
              </li>
            ))}
          </ul>
        </div>
      )}
    </>
  );
};


export default Navbar;
