    image: "/thumbnails/dead-dreams.png",
    category: "short-film",
    details: {
      date: "November 2024",
      location: "Gurgaon, India",
      company: "Independent",
      artist: "Harsh Pratap Singh",
      director: "Raghav / Ketan / Dhiren",
