import React from 'react';

const Skills = () => {
  const skillCategories = {
    "Filmmaking": [
      "Directing", "Cinematography", "Pre-production Planning", "On-set Art Direction"
    ],
    "Post-Production": [
      "Final Cut Pro", "CapCut", "Offline Editing", "Color Grading"
    ],
    "Photography & Visuals": [
      "Adobe Lightroom", "Snapseed", "Composition", "BTS & Set Photography"
    ],
    "Workflow & Strategy": [
      "Creative Problem Solving", "Team Management", "AI Integration", "Working Under Pressure"
    ]
  };

  return (
    <section className="skills">
      <h3>Skills</h3>
      <div className="skills-grid">
        {Object.entries(skillCategories).map(([category, skills], index) => (
          <div key={index} className="skill-category">
            <h4>{category}</h4>
            <ul>
              {skills.map((skill, idx) => (
                <li key={idx}>{skill}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Skills;
