import React, { useEffect, useState } from "react";

const PolaroidLoader = () => {
  const [photoIndex, setPhotoIndex] = useState(0);

  useEffect(() => {
    // Cycle photo index to trigger new photo animation every 2.5s
    const interval = setInterval(() => {
      setPhotoIndex((prev) => (prev + 1) % 5);
    }, 2500);

    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <style>{`
        .loader-wrapper {
          position: relative;
          width: 200px;
          height: 200px;
          margin: 50px auto;
          user-select: none;
        }
        /* Polaroid Camera */
        .camera {
          width: 150px;
          height: 100px;
          background: #0077ff;
          border-radius: 15px 15px 20px 20px;
          position: relative;
          box-shadow: 0 5px 15px rgba(0,0,0,0.3);
          margin: 0 auto;
        }
        /* Lens */
        .lens {
          width: 50px;
          height: 50px;
          background: #004bbd;
          border-radius: 50%;
          border: 5px solid #a3c1ff;
          position: absolute;
          top: 25px;
          left: 20px;
          box-shadow:
            inset 0 0 10px rgba(255,255,255,0.7),
            0 0 10px rgba(0,0,0,0.3);
        }
        /* Flash */
        .flash {
          position: absolute;
          top: 20px;
          right: 20px;
          width: 30px;
          height: 15px;
          background: #a3c1ff;
          border-radius: 3px;
          box-shadow: 0 0 15px 5px #a3c1ff;
          opacity: 0;
          animation: flashAnim 2.5s infinite;
        }
        @keyframes flashAnim {
          0%, 60%, 100% {opacity: 0;}
          62%, 70% {opacity: 1;}
        }

        /* Photo being printed out */
        .photo {
          position: absolute;
          top: 100%;
          left: 50%;
          width: 80px;
          height: 80px;
          background: white;
          border: 3px solid #a3c1ff;
          border-radius: 8px;
          transform: translateX(-50%);
          box-shadow: 0 0 15px rgba(0, 119, 255, 0.5);
          animation: photoPrint 2.5s forwards;
          overflow: visible;
          z-index: 10;
        }

        /* Photo content - blue gradient */
        .photo::before {
          content: "";
          position: absolute;
          top: 8px;
          left: 8px;
          right: 8px;
          bottom: 8px;
          background: linear-gradient(135deg, #0077ff 0%, #004bbd 100%);
          border-radius: 4px;
          filter: drop-shadow(0 0 4px #004bbd);
        }

        /* Dust particles container */
        .dust-container {
          position: absolute;
          top: 180px;
          left: 50%;
          width: 100px;
          height: 100px;
          transform: translateX(-50%);
          pointer-events: none;
          overflow: visible;
        }

        /* Dust particles */
        .dust {
          position: absolute;
          width: 6px;
          height: 6px;
          background: #0077ff;
          border-radius: 50%;
          opacity: 0;
          animation-fill-mode: forwards;
        }

        /* Dust animation for each particle with slight delay */
        ${[...Array(10)].map((_, i) => `
          .dust:nth-child(${i + 1}) {
            top: 50%;
            left: 50%;
            animation: dustAnim 2.5s forwards;
            animation-delay: ${(i * 0.2 + 1.5).toFixed(1)}s;
          }
          .dust:nth-child(${i + 1}) {
            transform-origin: center;
            animation-name: dustAnim${i + 1};
          }
          @keyframes dustAnim${i + 1} {
            0% {
              opacity: 1;
              transform: translate(0, 0) scale(1);
            }
            100% {
              opacity: 0;
              transform: translate(${(Math.random() * 80 - 40).toFixed(2)}px, ${(Math.random() * 80 - 40).toFixed(2)}px) scale(0.3);
            }
          }
        `).join("")}

        /* Photo print animation: move down and fade out */
        @keyframes photoPrint {
          0% {
            opacity: 1;
            transform: translateX(-50%) translateY(0);
          }
          70% {
            opacity: 1;
            transform: translateX(-50%) translateY(80px);
          }
          100% {
            opacity: 0;
            transform: translateX(-50%) translateY(100px);
          }
        }
      `}</style>

      <div className="loader-wrapper">
        <div className="camera">
          <div className="lens" />
          <div className="flash" />
        </div>

        {/* Animate photo print every time photoIndex changes */}
        <Photo key={photoIndex} />

        {/* Dust container */}
        <Dust />
      </div>
    </>
  );
};

const Photo = () => {
  return <div className="photo" />;
};

const Dust = () => {
  // Render 10 dust particles
  return (
    <div className="dust-container">
      {[...Array(10)].map((_, i) => (
        <div key={i} className="dust" />
      ))}
    </div>
  );
};

export default PolaroidLoader;
