import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import FeaturedWork from '../components/FeaturedWork';
import Skills from '../components/Skills';
import Contact from '../components/Contact';
import '../styles/main.css'

const Home = () => {
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true
  });

  const featuredWorks = [
    {
      id: 1,
      title: "Music Video - 'Ek Kamra'",
      role: "Director",
      year: "2025",
      description: "Directed and shot the video with a minimalist aesthetic and a strong emotional narrative",
      image: "/images/ek-kamra.jpg"
    },
    {
      id: 2,
      title: "BoxoutFM & Premise",
      role: "Videographer",
      year: "2024-2025",
      description: "Covered live DJ sets and captured underground electronic music culture",
      image: "/images/boxout.jpg"
    }
  ];

  return (
    <motion.div 
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="home"
    >
      <section className="hero">
        <motion.h1
          initial={{ y: -50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.8 }}
        >
          RAGHAV RAJ SOBTI
        </motion.h1>
        <motion.h2
          initial={{ y: 50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          PHOTOGRAPHER & VIDEOGRAPHER
        </motion.h2>
      </section>

      <section ref={ref} className="featured-works">
        <h3>Featured Works</h3>
        <div className="works-grid">
          {featuredWorks.map((work, index) => (
            <FeaturedWork 
              key={work.id}
              work={work}
              index={index}
              inView={inView}
            />
          ))}
        </div>
      </section>

      <Skills />
      <Contact />
    </motion.div>
  );
};

export default Home;
