import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';

const WorkDetail = () => {
  const { id } = useParams();
  const [work, setWork] = useState(null);
  const [loading, setLoading] = useState(true);

  // Mock data - replace with actual API call
  useEffect(() => {
    const works = [
      {
        id: '1',
        title: "Music Video - 'Ek Kamra'",
        role: "Director",
        year: "2025",
        description: "Directed and shot the video with a minimalist aesthetic and a strong emotional narrative",
        longDescription: "This music video was a passion project that explored the themes of solitude and self-discovery. Shot over 3 days in Mumbai using natural lighting and minimalist set design. The project challenged me to convey complex emotions through subtle camera movements and carefully composed frames.",
        embedUrl: "https://www.youtube.com/embed/YOUR_VIDEO_ID",
        images: [
          "/images/ek-kamra-1.jpg",
          "/images/ek-kamra-2.jpg",
          "/images/ek-kamra-3.jpg"
        ],
        credits: [
          "Producer: XYZ Productions",
          "Cinematographer: Raghav Raj Sobti",
          "Editor: Raghav Raj Sobti"
        ]
      },
      {
        id: '2',
        title: "BoxoutFM & Premise",
        role: "Videographer",
        year: "2024-2025",
        description: "Covered live DJ sets and captured underground electronic music culture",
        longDescription: "Documented the vibrant underground electronic music scene in Delhi, capturing performances by renowned artists and the energy of the crowd. Worked in low-light conditions to preserve the authentic atmosphere of the events while maintaining high production quality.",
        embedUrl: "https://www.youtube.com/embed/YOUR_VIDEO_ID_2",
        images: [
          "/images/boxout-1.jpg",
          "/images/boxout-2.jpg",
          "/images/boxout-3.jpg"
        ],
        credits: [
          "Artists: Laksh Maheshwari, Smoke, Rebel7",
          "Event: BoxoutFM Sessions"
        ]
      }
    ];

    const selectedWork = works.find(item => item.id === id);
    setWork(selectedWork);
    setLoading(false);
  }, [id]);

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!work) {
    return <div className="not-found">Work not found</div>;
  }

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="work-detail"
    >
      <div className="work-header">
        <h1>{work.title}</h1>
        <p className="meta">{work.role} • {work.year}</p>
      </div>

      <div className="video-container">
        <iframe
          src={work.embedUrl}
          title={work.title}
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></iframe>
      </div>

      <div className="work-content">
        <div className="description">
          <h2>Project Overview</h2>
          <p>{work.longDescription}</p>
        </div>

        <div className="gallery">
          <h2>Behind The Scenes</h2>
          <div className="image-grid">
            {work.images.map((image, index) => (
              <div key={index} className="image-item">
                <img src={image} alt={`${work.title} - ${index + 1}`} />
              </div>
            ))}
          </div>
        </div>

        <div className="credits">
          <h2>Credits</h2>
          <ul>
            {work.credits.map((credit, index) => (
              <li key={index}>{credit}</li>
            ))}
          </ul>
        </div>
      </div>
    </motion.div>
  );
};

export default WorkDetail;
